class BIK {
    static get REG_EXP() {
        return new RegExp('^[0-9]{2}[0-9]{2}[0-9]{2}[0-9]{3}$');
    }
    static validate(value) {
        if (value && !BIK.REG_EXP.test(value))
            return 'Введите число длиной 9 знаков';
        const regExp04Begin = new RegExp('^04');
        if (value && !regExp04Begin.test(value))
            return 'Первые две цифры в БИК должны быть «04»(код РФ)';
        return undefined;
    }
    static isValid(value) {
        return !BIK.validate(value);
    }
    static get hint() {
        return 'Начните вводить «Банковский идентификационный код» и выберите его из выпадающего списка';
    }
    static get placeholder() {
        return 'Введите БИК';
    }
    static get name() {
        return 'БИК';
    }
    static get field() {
        return 'bank';
    }
    static mask(value) {
        return value && value.replace(/\D/g, '').slice(0, 9);
    }
    constructor(bik) {
        if (BIK.isValid(`${bik}`)) {
            this._value = `${bik}`;
        }
        else {
            this._value = bik;
        }
    }
    clone() {
        return new BIK(this.value);
    }
    get value() {
        return this._value;
    }
}
export default BIK;
