            return '';
        return `${value}`.replace(/[^\d.]/g, '')
            .replace(/^([^.]*[.])|[.]/g, '$1');
    }
    static get hint() {
        return 'Введите стоимость товара без НДС';
    }
    static get placeholder() {
        return '0.00';
    }
    static get name() {
        return 'Стоимость без налога';
    }
    static get field() {
        return 'costBeforeTax';
    }
    constructor(value) {
        this._value = `${value}`;
    }
    get value() {
        return this._value;
    }
}
